import { ChainId, Currency, ETHER, Token } from 'ddswap-sdk'
import React, { useMemo } from 'react'
import styled from 'styled-components'

import EthereumLogo from '../../assets/images/ethereum-logo.png'
import ConfluxLogo from '../../assets/images/conflux-logo.png'
import HtLogo from '../../assets/images/ht-logo.png'
import useHttpLocations from '../../hooks/useHttpLocations'
import { WrappedTokenInfo } from '../../state/lists/hooks'
import Logo from '../Logo'

const getTokenLogoURL = (address: string) =>
  `https://www.dsme.vip/assest/ethereum/assest/${address}/logo.png`
  /*`https://raw.githubusercontent.com/trustwallet/assets/master/blockchains/ethereum/assets/${address}/logo.png`*/

const StyledEthereumLogo = styled.img<{ size: string }>`
  width: ${({ size }) => size};
  height: ${({ size }) => size};
  box-shadow: 0px 6px 10px rgba(0, 0, 0, 0.075);
  border-radius: 24px;
`

const StyledLogo = styled(Logo)<{ size: string }>`
  width: ${({ size }) => size};
  height: ${({ size }) => size};
`

export default function CurrencyLogo({
  currency,
  size = '24px',
  style
}: {
  currency?: Currency
  size?: string
  style?: React.CSSProperties
}) {
  const uriLocations = useHttpLocations(currency instanceof WrappedTokenInfo ? currency.logoURI : undefined)

  const srcs: string[] = useMemo(() => {
    if (currency === ETHER) return []

    if (currency instanceof Token) {
      if (currency instanceof WrappedTokenInfo) {
        return [...uriLocations, getTokenLogoURL(currency.address)]
      }

      return [getTokenLogoURL(currency.address)]
    }
    return []
  }, [currency, uriLocations])
/*
  if (currency === ETHER) {
    if(ETHER.symbol === "ETH" ){
      return <StyledEthereumLogo src={EthereumLogo} size={size} style={style} />
    }else if(ETHER.symbol === "CFX" ){
      return <StyledEthereumLogo src={ConfluxLogo} size={size} style={style} />
    }else if(ETHER.symbol === "HT" ){
      return <StyledEthereumLogo src={HtLogo} size={size} style={style} />
    }
  }
*/
  const coinsysbal: { [chainId in ChainId]: string } = {
    1: 'ETH',
    3: 'ETH',
    4: 'ETH',
    5: 'ETH',
    42: 'ETH',
    128: 'HT',
    1030: 'CFX',
    71:'CFX'
  }

    const getsymbal = `${coinsysbal[chainId] || coinsysbal[1]}`


  return <StyledLogo size={size} srcs={srcs} alt={`${currency?.symbol ?? 'token'} logo`} style={style} />
}
